                                if(num2 > parseInt(6)){
                                    num2 = num2 - parseInt(6);
                                }
                                var num3 = num2 + 1;
                                if(num3 > parseInt(6)){
                                    num3 = num3 - parseInt(6);
                                }
                            }else{
                                var num1 = parseInt(gift_detail.pos)+1;
                                var num2 = parseInt(gift_detail.pos)+1;
                                var num3 = parseInt(gift_detail.pos)+1;
                            }
                            doSlot(num1,num2,num3,num_roll_remain);
                        },
                        error: function(){
                            $('.content-popup').text('Có lỗi xảy ra. Vui lòng thử lại!');
                            $('#noticeModal').modal('show');
                        }
                    })
                }
            })

            function doSlot(one, two, three, num_roll_remain){
                document.getElementById("slot1").className='a1'
                document.getElementById("slot2").className='a1'
                document.getElementById("slot3").className='a1'
                var numChanges = randomInt(1,4)*6;
                var numeberSlot1 = numChanges+one
                var numeberSlot2 = numChanges+2*6+two
                var numeberSlot3 = numChanges+4*6+three
                var i1 = 0;
                var i2 = 0;
                var i3 = 0;
                var sound = 0
                //status.innerHTML = "SPINNING"
                slot1 = setInterval(spin1, 50);
                slot2 = setInterval(spin2, 50);
                slot3 = setInterval(spin3, 50);
                function spin1(){
                    i1++;
                    if (i1>=numeberSlot1){
                        clearInterval(slot1);
                        return null;
                    }
                    slotTile = document.getElementById("slot1");
                    if (slotTile.className==="a6"){
                        slotTile.className = "a0";
                    }
                    slotTile.className = "a"+(parseInt(slotTile.className.substring(1))+1)
                }
                function spin2(){
                    i2++;
                    if (i2>=numeberSlot2){
                        clearInterval(slot2);
                        return null;
                    }
                    slotTile = document.getElementById("slot2");
                    if (slotTile.className==="a6"){
                        slotTile.className = "a0";
                    }
                    slotTile.className = "a"+(parseInt(slotTile.className.substring(1))+1)
                }
                function spin3(){
                    i3++;
                    if (i3>=numeberSlot3){
                        clearInterval(slot3);
                        testWin();
                        return null;
                    }
                    slotTile = document.getElementById("slot3");
                    if (slotTile.className==="a6"){
                        slotTile.className = "a0";
                    }
                    slotTile.className = "a"+(parseInt(slotTile.className.substring(1))+1)
                }
            }

            function testWin(){
                roll_check = true;
                $("#btnWithdraw").show();
                console.log(gift_detail)
                if(gift_detail.locale === 1)
                {
                    $("#btnWithdraw").hide();
                }
                else
                {
                    if(gift_detail.input_auto === 0)
                    {
                        $("#btnWithdraw").html("Rút "+$("#withdrawruby_"+gift_detail.is_ruby).val());
                        $("#btnWithdraw").attr('href','/user/withdrawruby/'+gift_detail.is_ruby );
                    }
                    else if(gift_detail.input_auto === 1)
                    {
                        $("#btnWithdraw").html("Kiểm tra nick trúng");
                        $("#btnWithdraw").attr('href','/slotmachine/logaccgame/'+892);
                    }
                    else if(gift_detail.input_auto === $("#ID_NROCOIN").val())
                    {
                        $("#btnWithdraw").html("Rút vàng");
                        $("#btnWithdraw").attr('href','/user/withdrawservice/'+$("#ID_NROCOIN").val());
                    }
                    else if(gift_detail.input_auto === $("#ID_NROGEM").val())
                    {
                        $("#btnWithdraw").html("Rút ngọc");
                        $("#btnWithdraw").attr('href','/user/withdrawservice/'+$("#ID_NROGEM").val());
                    }
                    else if(gift_detail.input_auto === $("#ID_NINJAXU").val())
                    {
                        $("#btnWithdraw").html("Rút xu");
                        $("#btnWithdraw").attr('href','/user/withdrawservice/'+$("#ID_NINJAXU").val());
                    }
                    else if(gift_detail.input_auto === 2){
                        $("#btnWithdraw").html("Load lại trang");
                        $("#btnWithdraw").removeAttr("href");
                        $("#btnWithdraw").addClass('reLoad');
                    }else
                    {
                        $("#btnWithdraw").hide();
                    }

                }
                $('.content-popup').text('Kết quả: '+gift_detail.name);
                $('#noticeModal').modal('show');
                $('.num-play span').text(num_roll_remain);
            }

            $('body').delegate('.reLoad','click',function(){
                location.reload();
            })

            function randomExpert(min, max, expert, expert1){
                var value = Math.floor((Math.random() * (max-min+1)) + min);
                if(value === expert){
                    randomExpert(min, max, expert, expert1);
                }
                if(value === expert1){
                    randomExpert(min, max, expert, expert1);
                }
                return value;
            }

            function randomInt(min, max){
                return Math.floor((Math.random() * (max-min+1)) + min);
            }
        });
    </script>

