<input type="hidden" id="time_popup" value="">
<script>
    $(document).ready(function(){
        var time = $("#time_popup").val();
        if($("#time_popup").val()===''){
            time = 0;
        }
        if ($.cookie('noticeModal') !== '1' || time === 0) {
            //show popup here
            $('#noticeModal').addClass("in");
            $('#noticeModal').show() ;
            var date = new Date();
            var minutes = time*60;
            date.setTime(date.getTime() + (minutes * 60 * 1000));
            // alert(date);
            $.cookie('noticeModal', '1', { expires: date});
        }

        $("body").delegate('.close','click', function(){
            $('#noticeModal').removeClass("in");
            $('#noticeModal').hide() ;
        })

        $("body").delegate('.modal-footer .btn','click', function(){
            $('#noticeModal').removeClass("in");
            $('#noticeModal').hide() ;
        })
    });
</script>

